mapper.select:
		select d.*,(select p.name from sys_dept p where p.dept_id = d.parent_id) as parentName
		from sys_dept d where d.del_flag = 0

			and d.dept_id in (${deptFilter})



                order by d.${sidx} ${order}


                order by d.order_num asc



			limit #{offset}, #{limit}
